import React from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import Home from "../Comps/Home";
import DeckForm from "../Comps/Decks/CreateDeck";
import StudyDeck from "../Comps/Decks/StudyDeck";
import ViewDeck from "../Comps/Decks/ViewDeck";
import EditDeck from "../Comps/Decks/EditDeck";
import AddCard from "../Comps/Cards/AddCard";
import EditCard from "../Comps/Cards/EditCard";
import { Route, Switch, Redirect } from "react-router-dom";

function Layout() {
  return (
    <div>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            {/* shows a list of decks with options to create, study, view, or delete a deck*/}
            <Home />
          </Route>
          <Route exact path="/decks">
            <Redirect to="/" />
          </Route>

          <Route path="/decks/new">
            {/* route for creating a new deck */}
            <DeckForm />
          </Route>

          <Route exact path="/decks/:deckId">
            {/* route to view a specified deck */}
            <ViewDeck />
          </Route>

          <Route path="/decks/:deckId/study">
            {/* route to deck study home page */}
            <StudyDeck />
          </Route>

          <Route path="/decks/:deckId/edit">
            {/* route to edit a specified deck */}
            <EditDeck />
          </Route>

          <Route exact path="/decks/:deckId/cards/new">
            {/* route to add a new card to a specific deck */}
            <AddCard />
          </Route>

          <Route exact path="/decks/:deckId/cards/:cardId/edit">
            {/* route to edit a specific card in a deck */}
            <EditCard />
          </Route>
          <NotFound />
        </Switch>
      </div>
    </div>
  );
}

export default Layout;
