import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { listDecks, deleteDeck } from "../utils/api";

export default function Home() {
  const [decks, setDecks] = useState([]);
  const history = useHistory();

  //   useEffect(() => {
  //     const abortController = new AbortController();
  //     listDecks(abortController.signal)
  //       .then(setDecks)
  //       .catch(console.log("error"));
  //     return () => abortController.abort();
  //   });

  useEffect(() => {
    listDecks().then(setDecks);
  }, []);

  const handleDelete = (deckId) => {
    const confirmDelete = window.confirm(
      "Delete this deck? It will be unrecoverable."
    );
    if (confirmDelete) {
      deleteDeck(deckId);
      history.go();
    }
  };
  if (decks.length === 0) {
    return (
      <div>
        <p style={{ color: "red", fontSize: "1.5rem" }}>
          Fetching Decks! Please wait :)
        </p>
        <Link to="/decks/new">
          <button className="btn btn-secondary">
            <i class="bi bi-plus-lg"></i> Create Deck
          </button>
        </Link>
      </div>
    );
  }

  return (
    <div className="container">
      <Link to="/decks/new">
        <button className="btn">
          <i class="bi bi-plus-lg"></i> Create Deck
        </button>
      </Link>

      <div className="decks-list-home">
        {decks.map((deck) => (
          <div className="deck-home" key={deck.id}>
            <div className="deck-title-home">
              <h5>{deck.name}</h5>
              <p>{deck.cards.length} cards</p>
            </div>
            <p>{deck.description}</p>

            <div className="deck-btns-home">
              <div className="view-study">
                <Link to={`/decks/${deck.id}`}>
                  <button className="btn btn-secondary">View</button>
                </Link>
                <Link to={`/decks/${deck.id}/study`}>
                  <button className="btn btn-primary">Study</button>
                </Link>
              </div>
              <div className="delete-btn">
                <button
                  className="btn btn-danger"
                  onClick={() => handleDelete(deck.id)}
                >
                  <i class="bi bi-trash"></i>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
